/**
 * @desc Waits for the value of `window.location.href` to differ from
 * the supplied value due to browser history manipulation. If actual navigation
 * causes a change this function is useless.  The default poll rate is 1 second
 * and the safety timeout is 5 seconds.
 * @param {string} previousLocation - The value of `window.location.href` prior
 * to the pending or already occurred history manipulation of `window.location`
 * @param {{pollRate: ?number, safety: ?number}} [options] - Optional options
 * for controlling the checks poll rate and the safety timeout length.
 * Supplied values for `pollRate` and `safety` in milliseconds.
 * @return {Promise<boolean>} - Returns false if no change was detected by
 * the timeout length determined by options.safety otherwise true.
 */
export function waitForHistoryManipToChangeLocation(previousLocation, options) {
  const opts = Object.assign({ pollRate: 1000, safety: 5000 }, options);
  let pollInterval;
  let safetyTimeout;
  return new Promise(resolve => {
    if (window.location.href !== previousLocation) return resolve(true);
    pollInterval = setInterval(() => {
      if (window.location.href !== previousLocation) {
        if (safetyTimeout) {
          clearTimeout(safetyTimeout);
        }
        clearInterval(pollInterval);
        return resolve(true);
      }
    }, opts.pollRate);
    safetyTimeout = setTimeout(() => {
      if (pollInterval) clearInterval(pollInterval);
      resolve(window.location.href !== previousLocation);
    }, opts.safety);
  });
}

/**
 * @desc Determines the strict equality of the string
 * to the value of `window.location.href`
 * @param {string} someLocation - The expect location.href value
 * @return {boolean}
 */
export function locationEquals(someLocation) {
  return window.location.href === someLocation;
}

/**
 * @desc Determines if `window.location.href` contains
 * the supplied value as a substring.
 * @param {string} something - The value to determine if
 * it is a substring of `window.location.href`
 * @return {boolean}
 */
export function locationContains(something) {
  return window.location.href.indexOf(something) !== -1;
}

import { delay, waitForPredicate } from './delays';
import { scrollIntoView } from './scrolls';
import { qs } from './dom';
import { createMouseEvent, fireMouseEventsOnElement } from './events';

let __currentClickCount = 0;
const __clickPreEvents = ['mousemove', 'mouseover', 'mousedown', 'mouseup'];

/**
 * @desc Calls the click function on the supplied element if non-null/defined.
 * Returns true or false to indicate if the click happened
 * @param {HTMLElement} elem - The element to be clicked
 * @return {boolean} - T/F to indicate that click happened.
 */
export function click(elem) {
  let clicked = false;
  if (elem != null) {
    fireMouseEventsOnElement({
      elem,
      eventNames: __clickPreEvents,
      clickCount: __currentClickCount
    });
    __currentClickCount += 1;
    elem.click();
    elem.dispatchEvent(
      createMouseEvent({
        type: 'mouseleave',
        elem,
        clickCount: __currentClickCount
      })
    );
    clicked = true;
  }
  return clicked;
}

/**
 * @desc Calls the click function on the supplied element if non-null/defined
 * that exists in the JS context of the supplied window (cntx).
 * Returns true or false to indicate if the click happened
 * @param {HTMLElement} elem - The element to be clicked
 * @param {Window} cntx - The context window
 * @return {boolean} - T/F to indicate that click happened.
 */
export function clickInContext(elem, cntx) {
  let clicked = false;
  if (elem != null) {
    fireMouseEventsOnElement({
      elem,
      eventNames: __clickPreEvents,
      view: cntx
    });
    elem.click();
    elem.dispatchEvent(
      createMouseEvent({ type: 'mouseleave', view: cntx, elem })
    );
    clicked = true;
  }
  return clicked;
}

/**
 * @desc Calls the click method of the supplied element and waits the
 * specified delayTime after clicking IFF the supplied element was
 * non-null/undefined.
 * @param {*} elem - The element to be clicked
 * @param {number} [delayTime = 1000] - How long is the delay after click
 * @return {Promise<boolean>} - A Promise that resolves to T/F
 * indicating if the click happened
 */
export async function clickWithDelay(elem, delayTime) {
  let clicked = click(elem);
  if (clicked) {
    await delay(delayTime || 1000);
  }
  return clicked;
}

/**
 * @desc Calls the click method of the supplied element and waits the
 * specified delayTime after clicking IFF the supplied element was
 * non-null/undefined.
 * @param {*} elem - The element to be clicked
 * @param {{delayTime: number, cntx: ?Window}} [options] - Options controlling
 * how long the delay is (delay time) and or a supplied window object of an iframe
 * we are clicking an element within (cntx)
 * @return {Promise<boolean>} - A Promise that resolves to T/F
 * indicating if the click happened
 */
export async function clickWithDelay2(elem, options) {
  const opts = Object.assign({ delayTime: 1000 }, options);
  const clicked = opts.cntx ? clickInContext(elem, opts.cntx) : click(elem);
  if (clicked) {
    await delay(opts.delayTime);
  }
  return clicked;
}

/**
 * @desc Calls the click method of the supplied element that exists in
 * the JS context of the supplied window object (cntx) and waits the
 * specified delayTime after clicking IFF the supplied element was
 * non-null/undefined.
 * @param {HTMLElement|Element} elem - The element to be clicked
 * @param {Window} cntx - The context window
 * @param {number} [delayTime = 1000] - How long is the delay after click
 * @return {Promise<boolean>} - A Promise that resolves to T/F
 * indicating if the click happened
 */
export async function clickInContextWithDelay(elem, cntx, delayTime) {
  let clicked = clickInContext(elem, cntx);
  if (clicked) {
    await delay(delayTime || 1000);
  }
  return clicked;
}

/**
 * @desc Scrolls the supplied element into view and clicks it if the
 * element is non-null/undefined
 * @param {HTMLElement|Element} elem - The element to be scrolled into view
 * and clicked.
 * @return {boolean} - T/F to indicate that the click happened
 */
export function scrollIntoViewAndClick(elem) {
  scrollIntoView(elem);
  return click(elem);
}

/**
 * @desc Scrolls the supplied element into view and clicks it if the
 * element is non-null/undefined
 * @param {HTMLElement|Element} elem - The element to be scrolled into view
 * and clicked.
 * @param {Window} cntx - Optional window object associated with the iframe
 * the element lives in
 * @return {boolean} - T/F to indicate that the click happened
 */
export function scrollIntoViewAndClick2(elem, cntx) {
  scrollIntoView(elem);
  return cntx != null ? clickInContext(elem, cntx) : click(elem);
}

/**
 * @desc Scrolls the supplied element into view and clicks it if the
 * element is non-null/undefined and waits for the specified delay time
 * @param {HTMLElement|Element} elem - The element to be
 * @param {number} [delayTime = 1000] - How long is the delay
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating
 * that the click happened
 */
export function scrollIntoViewAndClickWithDelay(elem, delayTime) {
  scrollIntoView(elem);
  return clickWithDelay(elem, delayTime || 1000);
}

/**
 * @desc Scrolls the supplied element into view and clicks it if the
 * element is non-null/undefined and waits for the specified delay time
 * @param {HTMLElement|Element} elem - The element to be
 * @param {{delayTime: number, cntx: Window}} [options] - Options controlling
 * how long the delay is (delay time) and or a supplied window object of an iframe
 * we are clicking an element within (cntx)
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating
 * that the click happened
 */
export function scrollIntoViewAndClickWithDelay2(elem, options) {
  const opts = Object.assign({ delayTime: 1000 }, options);
  scrollIntoView(elem);
  return opts.cntx != null
    ? clickInContextWithDelay(elem, opts.cntx, opts.delayTime)
    : clickWithDelay(elem, opts.delayTime);
}

/**
 * @desc Scrolls into view and clicks all the elements
 * present in the supplied array.
 * @param {Array<HTMLElement>} elems - The array of elements
 * to be scrolled into view and clicked
 * @return {void}
 */
export function scrollAllIntoViewAndClick(elems) {
  let i = 0;
  let length = elems.length;
  for (; i < length; ++i) {
    scrollIntoView(elems[i]);
    click(elems[i]);
  }
}

/**
 * @desc Scrolls into view and clicks all the elements
 * present in the supplied array waiting for the supplied
 * delay time after each click of an element in the array.
 * @param {Array<HTMLElement|Element|Node>} elems - The array of elements
 * to be scrolled into view and clicked
 * @param {number} [delayTime = 1000] - How long is the delay after click
 * @return {Promise<void>}
 */
export async function scrollAllIntoViewAndClickWithDelay(elems, delayTime) {
  const delayAmount = delayTime || 1000;
  const length = elems.length;
  for (var i = 0; i < length; ++i) {
    scrollIntoView(elems[i]);
    await clickWithDelay(elems[i], delayAmount);
  }
}

/**
 * @desc Clicks the element from executing document.querySelector using
 * the supplied selector
 * @param {string} selector - the css selector to use
 * @return {boolean} - T/F to indicate that the click happened
 */
export function selectElemAndClick(selector) {
  return click(document.querySelector(selector));
}

/**
 * @desc Clicks the element from executing document.querySelector using
 * the supplied selector and waiting the specified delay time after clicking
 * @param {string} selector - the css selector to use
 * @param {number} [delayTime = 1000] - How long is the delay
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating
 * that the click happened
 */
export function selectElemAndClickWithDelay(selector, delayTime) {
  return clickWithDelay(document.querySelector(selector), delayTime || 1000);
}

/**
 * @desc Clicks the element from executing querySelector using
 * the supplied selector using the supplied element
 * @param {Element|Node|HTMLElement} selectFrom - element to use for the querySelector call
 * @param {string} selector - the css selector to use
 * @return {boolean} - T/F to indicate that the click happened
 */
export function selectElemFromAndClick(selectFrom, selector) {
  return click(selectFrom.querySelector(selector));
}

/**
 * @desc Clicks the element from executing querySelector using
 * the supplied selector from the supplied element and waiting the specified delay time after clicking
 * @param {Element|Node|HTMLElement} selectFrom - element to use for the querySelector call
 * @param {string} selector - the css selector to use
 * @param {number} [delayTime = 1000] - How long is the delay
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating
 * that the click happened
 */
export function selectElemFromAndClickWithDelay(
  selectFrom,
  selector,
  delayTime
) {
  return clickWithDelay(qs(selector, selectFrom), delayTime || 1000);
}

/**
 * @desc Clicks the supplied element and then waits for the supplied predicate function to return true
 * @param {Element|Node|HTMLElement} elem - the element to be clicked
 * @param {function(): boolean} predicate - function returning true or false indicating the wait condition is satisfied
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating that the click happened
 */
export async function clickAndWaitFor(elem, predicate) {
  const clicked = click(elem);
  if (clicked) {
    await waitForPredicate(predicate);
  }
  return clicked;
}

/**
 * @desc Clicks the element returned by calling querySelector using the selector
 * on document or the supplied element (selectFrom) and then waits for the supplied
 * predicate function to return true
 * @param {string} selector - the css selector to use
 * @param {function(): boolean} predicate - function returning true or false indicating the wait condition is satisfied
 * @param {HTMLElement|Document} [selectFrom] - optional element to use for the querySelector(selector) call
 * @return {Promise<boolean>} - A Promise that resolves to T/F indicating that the click happened
 */
export function selectClickAndWaitFor(selector, predicate, selectFrom) {
  return clickAndWaitFor(qs(selector, selectFrom), predicate);
}

/**
 * @desc Clicks the element returned from result of calling querySelector using the supplied
 * selector on the supplied element N number of times. Note that the evaluation of the querySelector
 * is done all N times.
 * @param {Element} selectFrom - element to use for the querySelector(selector) call
 * @param {string} selector - the css selector to use
 * @param {number} n - How many times to click the element returned by
 * performing selectFrom.querySelector(selector)
 */
export function selectFromAndClickNTimes(selectFrom, selector, n) {
  let clickMe;
  for (var i = 0; i < n; ++i) {
    clickMe = selectFrom.querySelector(selector);
    if (clickMe) {
      click(clickMe);
    }
  }
}

/**
 * @desc Clicks the element returned from result of calling querySelector using the supplied
 * selector on the supplied element N number of times and waits for delay time afters each click.
 * Note that the evaluation of the querySelector is done all N times.
 * @param {Element} selectFrom - The element to use to call querySelector(selector) on
 * @param {string} selector - the css selector to use
 * @param {number} n - How many times to click the element returned by
 * performing selectFrom.querySelector(selector)
 * @param {number} [delayTime = 1000] - How long is the delay
 */
export async function selectFromAndClickNTimesWithDelay(
  selectFrom,
  selector,
  n,
  delayTime
) {
  const delayAmount = delayTime || 1000;
  let clickMe;
  for (var i = 0; i < n; ++i) {
    clickMe = selectFrom.querySelector(selector);
    if (clickMe) {
      await clickWithDelay(clickMe, delayAmount);
    }
  }
}

/**
 * @desc Clicks the element returned from result of calling querySelector using the supplied
 * selector on the supplied element until the results of the evaluting the selector returns null/undefined,
 * waiting for delay time afters each click.
 * @param {Element} selectFrom - The element to use to call querySelector(selector) on
 * @param {string} selector - the css selector to use
 * performing selectFrom.querySelector(selector)
 * @param {{delayTime: number, safety: ?number}} [options] - options about delayTime and safety time
 */
export async function selectFromAndClickUntilNullWithDelay(
  selectFrom,
  selector,
  options
) {
  const opts = Object.assign({ delayTime: 1000 }, options);
  const delayTime = opts.delayTime;
  let exit = false;
  let safety;
  if (opts.safety) {
    safety = setTimeout(() => {
      exit = true;
    }, opts.safety);
  }
  let clickMe = selectFrom.querySelector(selector);
  while (clickMe != null) {
    if (exit) break;
    await clickWithDelay(clickMe, delayTime);
    clickMe = selectFrom.querySelector(selector);
  }
  if (opts.safety) {
    clearTimeout(safety);
  }
}

import { qs, selectorExists } from './dom';

/**
 * @desc 1 second in milliseconds
 * @type {number}
 */
export const DelayAmount1Second = 1000;

/**
 * @desc 2the second in milliseconds
 * @type {number}
 */
export const DelayAmount2Seconds = 2000;

/**
 * @desc 3 second in milliseconds
 * @type {number}
 */
export const DelayAmount3Seconds = 3000;

/**
 * @desc 4 second in milliseconds
 * @type {number}
 */
export const DelayAmount4Seconds = 4000;

/**
 * @desc 5 second in milliseconds
 * @type {number}
 */
export const DelayAmount5Seconds = 5000;

/**
 * @desc Converts the supplied n number of seconds into milliseconds
 * which is required for setTimeout and setInterval
 * @param {number} n - The number of seconds to be delayed
 * @return {number} - The delay amount in milliseconds
 */
export function secondsToDelayAmount(n) {
  return n * 1000;
}

/**
 * @desc Returns a Promise that resolves once
 * the behavior has been un-paused
 * @return {Promise<any>}
 */
export function resolveWhenBehaviorUnPaused() {
  return new Promise(resolve => {
    let intervalId = setInterval(() => {
      if (!window.$WBBehaviorPaused) {
        clearInterval(intervalId);
        resolve();
      }
    }, 2000);
  });
}

/**
 * @desc Polls the supplied function at the timeout rate
 * only if the behavior is not paused
 * @return {number} - The id of the interval
 */
export function setIntervalP(callback, timeout) {
  return setInterval(function() {
    if (!window.$WBBehaviorPaused) {
      callback();
    }
  }, timeout);
}

/**
 * @desc Like setTimeout except that the timeout respects the pause state
 * of the behavior
 * @return {number} - The id of the timeout
 */
export function setTimeoutP(callback, timeout) {
  function execIfNotPaused() {
    if (!window.$WBBehaviorPaused) {
      callback();
    } else {
      setTimeout(execIfNotPaused, 500);
    }
  }

  return setTimeout(execIfNotPaused, timeout);
}

/**
 * @desc Returns a Promise that resolves after waiting the amount of time
 * specified by delayTime.
 * @param {number} [delayTime = 3000] - The length of the delay in milliseconds
 * @return {Promise<void>}
 */
export function delay(delayTime) {
  return new Promise(resolve => {
    setTimeout(resolve, delayTime || 3000);
  });
}

/**
 * @desc Returns a promise that resolves once the supplied predicate function
 * returns a truthy value.
 * @param {function(): boolean} predicate - A predicate function that
 * returns a truthy value to indicate the wait condition was satisfied otherwise
 * a falsy value to indicate wait condition not satisfied
 * @param {number} [pollRate = 1000] - The length of time, in milliseconds,
 * that the predicate is to be polled at
 * @return {Promise<void>}
 */
export function waitForPredicate(predicate, pollRate) {
  return new Promise(resolve => {
    let int = setInterval(() => {
      if (predicate()) {
        clearInterval(int);
        resolve();
      }
    }, pollRate || 1000);
  });
}

/**
 * @desc Returns a promise that resolves when the supplied predicate function
 * returns a truthy value or the maximum wait time has been reached.
 * @param {function(): boolean} predicate - A predicate function that
 * returns a truthy value to indicate the wait condition was satisfied otherwise
 * a falsy value to indicate wait condition not satisfied
 * @param {number|{pollRate: ?number, max: ?number}} options - Either a
 * number (time in milliseconds) representing the maximum wait time for the
 * supplied predicate to return a truthy value or an object with properties
 * pollRate (optional, controls the interval rate the supplied predicate will be polled
 * at), max (how long to wait for the supplied predicate to return a truthy value)
 * @return {Promise<void>}
 */
export function waitForPredicateAtMax(predicate, options) {
  const opts = Object.assign(
    { pollRate: 1000 },
    typeof options === 'number' ? { max: options } : options
  );
  return new Promise(resolve => {
    let to = -1;
    let int = setInterval(() => {
      if (predicate()) {
        clearTimeout(to);
        clearInterval(int);
        resolve();
      }
    }, opts.pollRate);
    to = setTimeout(() => {
      clearInterval(int);
      resolve();
    }, opts.max);
  });
}

/**
 * @desc Calls `fromNode.querySelector` using the supplied selector. If the
 * return value of the first calls is null/undefined then waits for the same call
 * to return a truthy value and then returns the element the selector matches.
 * @param {Element|Node|HTMLElement} fromNode - The element the
 * to select `selector` from
 * @param {string} selector - The querySelector to use for `fromNode.querySelector`
 * call.
 * @param {number} [pollRate = 1000] - The length of time, in milliseconds,
 * that the predicate is to be polled at
 * @return {Promise<Element|Node|HTMLElement>}
 */
export async function waitForAndSelectElement(fromNode, selector, pollRate) {
  const elem = qs(selector, fromNode);
  if (!elem) {
    await waitForPredicate(
      () => selectorExists(selector, fromNode),
      pollRate || 1000
    );
    return qs(selector, fromNode);
  }
  return elem;
}

/**
 * @desc Returns a promise that resolves once
 * `document.readyState === 'complete'`
 * @return {Promise<void>}
 */
export function domCompletePromise() {
  if (document.readyState !== 'complete') {
    return new Promise(r => {
      let i = setIntervalP(() => {
        if (document.readyState === 'complete') {
          clearInterval(i);
          r();
        }
      }, 1000);
    });
  }
  return Promise.resolve();
}

/**
 * @desc Waits for the number children of the supplied parentElement
 * to become greater than the supplied `currentChildCount`
 * @param {Element} parentElement - The parent
 * @param {number} currentChildCount
 * @param {{pollRate: number, max: ?number}} [options]
 * @return {Promise<void>}
 */
export function waitForAdditionalElemChildren(
  parentElement,
  currentChildCount,
  options
) {
  const opts = Object.assign({ pollRate: 1000, max: 8 }, options);
  let n = 0;
  let int = -1;
  return new Promise(resolve => {
    int = setInterval(() => {
      if (!parentElement.isConnected) {
        clearInterval(int);
        return resolve();
      }
      if (
        parentElement.children &&
        parentElement.children.length > currentChildCount
      ) {
        clearInterval(int);
        return resolve();
      }
      if (n > opts.max) {
        clearInterval(int);
        return resolve();
      }
      n += 1;
    }, opts.pollRate);
  });
}

import { camelCase } from './strings';

/**
 * @desc Returns the results of evaluating the supplied
 * xpath query using an optional context `contextElement`, defaults
 * to document, as XPathResult.ORDERED_NODE_SNAPSHOT_TYPE
 * @param {string} xpathQuery - The xpath query to be evaluated
 * @param {Element|Document} [contextElement] - Optional
 * element to be used as the context of the evaluation
 * @return {XPathResult} - The results of the xpath query evaluation
 * @see https://developer.mozilla.org/en-US/docs/Web/API/Document/evaluate
 */
export function xpathSnapShot(xpathQuery, contextElement) {
  if (contextElement == null) {
    contextElement = document;
  }
  return document.evaluate(
    xpathQuery,
    contextElement,
    null,
    XPathResult.ORDERED_NODE_SNAPSHOT_TYPE,
    null
  );
}

/**
 * @desc Provides the same functionality of the chrome console utility `$x`
 * but likely less performant
 * @param {string} xpathQuery - The xpath query to be evaluated
 * @param {Element|Document} [contextElement] - Optional
 * element to be used as the context of the evaluation
 * @return {Array<HTMLElement>} - The results of the xpath query evaluation
 * @see https://developer.mozilla.org/en-US/docs/Web/API/Document/evaluate
 * @see https://developers.google.com/web/tools/chrome-devtools/console/utilities
 */
export function xpathSnapShotArray(xpathQuery, contextElement) {
  const snapShot = xpathSnapShot(xpathQuery, contextElement);
  const elements = [];
  let i = 0;
  let len = snapShot.snapshotLength;
  while (i < len) {
    elements.push(snapShot.snapshotItem(i));
    i += 1;
  }
  return elements;
}

/**
 * @desc Ensures that if the value of the chrome console utility $x
 * is not the actual utility (jquery is on the page) the returned
 * function behaves exactly like it.
 * @param {function} cliXPG
 * @return {function: Array<HTMLElement>}
 */
export function maybePolyfillXPG(cliXPG) {
  if (
    typeof cliXPG !== 'function' ||
    cliXPG.toString().indexOf('[Command Line API]') === -1
  ) {
    return xpathSnapShotArray;
  }
  return cliXPG;
}

/**
 * @desc Utility function for `(document||element).querySelector(selector)`
 * @param {string} selector - the selector to be use
 * @param {Element|Node|HTMLElement|Document} [context] - element to use rather than document for the querySelector call
 * @return {Element|Node|HTMLElement|HTMLIFrameElement}
 */
export function qs(selector, context) {
  if (context != null) return context.querySelector(selector);
  return document.querySelector(selector);
}

/**
 * @desc Utility function for `(document||element).querySelector(selector)`
 * @param {string} selector - the selector to be use
 * @param {function(elem: SomeElement): boolean} filterFn
 * @param {Element|Node} [context]
 * @return {Element|Node|HTMLElement|HTMLIFrameElement}
 */
export function filteredQs(selector, filterFn, context) {
  const elem = qs(selector, context);
  if (elem == null) return null;
  if (elem && filterFn(elem)) return elem;
  return null;
}

/**
 * @desc Utility function for `document.querySelectorAll(selector)`
 * @param {string} selector - the selector to be use
 * @param {Element|Node|HTMLElement|Document} [context] - element to use rather than document for the querySelector call
 * @return {NodeList<Element|Node|HTMLElement>}
 */
export function qsa(selector, context) {
  if (context != null) return context.querySelectorAll(selector);
  return document.querySelectorAll(selector);
}

/**
 * @desc Utility function for `document.getElementById(id)`
 * @param {string} eid - The id of the element to get
 * @param {?Document} [context] - Optional document element to use rather than
 * the current JS context's
 * @return {?HTMLElement}
 */
export function id(eid, context) {
  if (context != null) return context.getElementById(eid);
  return document.getElementById(eid);
}

/**
 * @desc Removes the element selected by the supplied querySelector, if it exits,
 * returning true to indicate the element was removed and false otherwise
 * @param {string} selector - the selector to be use
 * @param {Element|Node|HTMLElement} [context] - element to use rather than document for the querySelector call
 * @return {boolean}
 */
export function maybeRemoveElem(selector, context) {
  const elem = qs(selector, context);
  if (elem) {
    elem.remove();
    return true;
  }
  return false;
}

/**
 * @desc Removes the element with the supplied id, if it exits, returning
 * true to indicate the element was removed and false otherwise
 * @param {string} eid - The id of the element to remove
 * @param {?Document} [context] - Optional document element to use rather than
 * the current JS context's
 * @return {boolean}
 */
export function maybeRemoveElemById(eid, context) {
  const elem = id(eid, context);
  if (elem) {
    elem.remove();
    return true;
  }
  return false;
}

/**
 * @desc Returns true if the supplied elements `offsetTop === 0`
 * @param {Element|HTMLElement|Node} elem - The element to check its
 * offsetTop
 * @return {boolean}
 */
export function elemOffsetTopZero(elem) {
  return elem.offsetTop === 0;
}

/**
 * @desc Marks the supplied element as visited by adding the marker
 * to its classList.
 * @param {HTMLElement|Element|Node} elem - The element to mark
 * as visited
 * @param {string} [marker = 'wrvistited'] - Optional marker to use
 * defaults to `wrvistited`
 */
export function markElemAsVisited(elem, marker = 'wrvistited') {
  if (elem != null) {
    elem.classList.add(marker);
  }
}

/**
 * @desc Creates a style tag if one was not created before and adds
 * the supplied `styleDef` to it. If a style tag was created before
 * this function is a no-op
 * @param {string} styleDef - The CSS rules to add
 * @return {Object} - An object containing the selectors as key value pairs
 * where the keys are the selectors in camelcase and the value is the raw selector
 */
export function addBehaviorStyle(styleDef) {
  let style = document.getElementById('$wrStyle$');
  if (style == null) {
    style = document.createElement('style');
    style.id = '$wrStyle$';
    style.textContent = styleDef;
    document.head.appendChild(style);
  }
  const rules = style.sheet.rules;
  let ruleIdx = rules.length;
  let selector;
  const classes = {};
  while (ruleIdx--) {
    selector = rules[ruleIdx].selectorText.replace('.', '');
    classes[camelCase(selector)] = selector;
  }
  return classes;
}

/**
 * @desc Determines if the supplied iframe is accessible from this
 * origin. Test is that access the window object of the iframes contentWindow
 * does not throw an exception and the contentDocument is not falsy.
 * @param {HTMLIFrameElement} iframe - The iframe to determine accessibility
 * @return {boolean} - True if the iframe is accessible and false otherwise
 */
export function canAcessIf(iframe) {
  if (iframe == null) return false;
  try {
    iframe.contentWindow.window;
  } catch (e) {
    return false;
  }
  return iframe.contentDocument != null;
}

/**
 * @desc Determines if the element the supplied selector selects exists
 * @param {string} selector - The querySelector to use for testing if
 * the element it selects exists
 * @param {Document|Element} [cntx] - Optional element to use rather
 * than the current JS context's document object
 * @return {boolean} - True if the element exists, false otherwise
 */
export function selectorExists(selector, cntx) {
  return qs(selector, cntx) != null;
}

/**
 * @desc Determines if the element the supplied id identifies exists
 * @param {string} eid - The id of the element
 * @param {Document} [cntx] - Optional document object to use rather
 * than the current JS context's document object
 * @return {boolean} - True if the element exists, false otherwise
 */
export function idExists(eid, cntx) {
  return id(eid, cntx) != null;
}

/**
 * @desc Attempts to find a tag using the supplied function that accepts
 * an xpath query and an optional starting element and returns
 * the element the supplied predicate function returns a truthy value for
 * @param {function(xpathQuery: string, startElem: ?Node): Node[]} xpg - xpath execution function
 * @param {string} tag - The tag to be found
 * @param {function(elem: Node): boolean} predicate - Element selecting predicate function
 * @param {Document|Node} [cntx] - Optional starting element, defaults to `document`
 * @return {?Element|?HTMLIFrameElement|?Node} - The desired element if it was found
 */
export function findTag(xpg, tag, predicate, cntx) {
  const tags = xpg(`//${tag}`, cntx || document);
  const len = tags.length;
  for (var i = 0; i < len; ++i) {
    if (predicate(tags[i])) return tags[i];
  }
  return null;
}

/**
 * @desc Retrieves the value of an elements attribute if it exists
 * @param {Element} elem - The element to retrieve an attribute from
 * @param {string} attr - The name of the attribute to be retrieved
 * @return {?string|?Object} - The value of the retrieved attribute if it exists
 */
export function attr(elem, attr) {
  if (elem) return elem.getAttribute(attr);
  return null;
}

/**
 * @desc Tests to determine if the value of elements attribute equals
 * the supplied value using loose equality
 * @param {Element} elem - The element to retrieve an attribute from
 * @param {string} attr - The name of the attribute to be retrieved
 * @param {*} shouldEq - The value the attributes value should equal
 * @return {boolean} - T/F indicating if the attribute equals. Note
 * false can indicate the attribute does not equal expected or
 * the element was null/undefined
 */
export function attrEq(elem, attr, shouldEq) {
  if (elem) return elem.getAttribute(attr) == shouldEq;
  return false;
}

/**
 * @desc Returns the Nth child of the supplied element (indexing assumes start is 1)
 * @param {Element|Document} elem - The element to retrieve the nth child of
 * @param {number} nth - The number of the nth child
 * @return {?Element} - The nth child if it exists
 */
export function nthChildElemOf(elem, nth) {
  if (elem && elem.children && elem.children.length >= nth) {
    return elem.children[nth - 1];
  }
  return null;
}

/**
 *
 * @param {Element} startingElem
 * @param {...number} nths
 * @return {?Element}
 */
export function chainNthChildElemOf(startingElem, ...nths) {
  let child = startingElem;
  if (
    startingElem != null &&
    startingElem.children != null &&
    startingElem.children.length
  ) {
    const length = nths.length;
    for (var i = 0; i < length; ++i) {
      child = nthChildElemOf(child, nths[i]);
      if (child == null) break;
    }
  }
  return child;
}

/**
 *
 * @param {Element} elem
 * @returns {?Element}
 */
export function firstChildElementOf(elem) {
  if (elem != null && elem.firstElementChild) {
    return elem.firstElementChild;
  }
  return null;
}

/**
 *
 * @param {Element} elem
 * @param {number} times
 * @returns {?Element}
 */
export function chainFistChildElemOf(elem, times) {
  let child = elem;
  if (elem != null) {
    for (var i = 0; i < times; ++i) {
      child = firstChildElementOf(child);
      if (child == null) break;
    }
  }
  return child;
}

/**
 *
 * @param {Element} elem
 * @returns {?Element}
 */
export function lastChildElementOf(elem) {
  if (elem != null && elem.children && elem.children.length) {
    return elem.children[elem.children.length - 1];
  }
  return null;
}

/**
 *
 * @param {Element} elem
 * @param {number} times
 * @returns {?Element}
 */
export function chainLastChildElemOf(elem, times) {
  let child = elem;
  if (elem != null && elem.children && elem.children.length) {
    for (var i = 0; i < times; ++i) {
      child = lastChildElementOf(child);
      if (child == null) break;
    }
  }
  return child;
}

/**
 *
 * @param {Element} elem
 * @return {number}
 */
export function numElemChildren(elem) {
  if (elem != null) {
    return elem.childElementCount;
  }
  return -1;
}

/**
 * @desc Consecutively calls querySelector(selector) on the element returned by the previous invocation
 * @param {Element|Node|Document} startingSelectFrom - The first element to perform querySelector(startingSelector) on
 * @param {string} startingSelector - The first selector
 * @param {...string} selectors - Additional selections
 * @return {?Element} - Final selected element if it exists
 */
export function chainQs(startingSelectFrom, startingSelector, ...selectors) {
  let selected = qs(startingSelector, startingSelectFrom);
  if (selected != null) {
    const len = selectors.length;
    for (var i = 0; i < len; ++i) {
      selected = qs(selectors[i], selected);
      if (selected == null) return null;
    }
  }
  return selected;
}

/**
 * @desc Adds a CSS classname to the supplied element
 * @param {?Element} elem - The element to add the classname to
 * @param {string} clazz - The classname to be added
 */
export function addClass(elem, clazz) {
  if (elem) {
    elem.classList.add(clazz);
  }
}

/**
 * @desc Removes a CSS classname to the supplied element
 * @param {?Element} elem - The element to remove the classname to
 * @param {string} clazz - The classname to be removed
 */
export function removeClass(elem, clazz) {
  if (elem) {
    elem.classList.remove(clazz);
  }
}

/**
 * @desc Tests to see if the supplied element has a css class
 * @param {?Element} elem - The element to be tested if it has the class
 * @param {string} clazz - The classname
 * @return {boolean} - T/F indicating if the element has the class
 */
export function hasClass(elem, clazz) {
  if (elem) return elem.classList.contains(clazz);
  return false;
}

/**
 *
 * @param {Element} elem
 * @return {boolean}
 */
export function isClasslessElem(elem) {
  return elem.classList.length === 0;
}

/**
 * @desc Returns the supplied elements next element sibling
 * @param {Element|Node} elem - The element to receive its sibling
 * @return {?Element} - The elements sibling if it exists
 */
export function getElemSibling(elem) {
  return elem.nextElementSibling;
}

/**
 *
 * @param {Element|Node} elem
 * @return {boolean}
 */
export function elemHasSibling(elem) {
  return getElemSibling(elem) != null;
}

/**
 * @desc Returns the supplied elements next element sibling and removes the
 * supplied element
 * @param {Element|Node} elem - The element to receive its sibling
 * @return {?Element} - The elements sibling if it exists
 */
export function getElemSiblingAndRemoveElem(elem) {
  const sibling = getElemSibling(elem);
  elem.remove();
  return sibling;
}

/**
 * @desc Determines if the supplied elements bounding client rect's
 * x,y,width,height,top,left properties all equal zero.
 * Note this function returns true if the element is null/undefined;
 * @param {Element} elem - The element to be tested
 * @return {boolean} - T/F indicating if all zero or not.
 */
export function elemHasZeroBoundingRect(elem) {
  if (elem == null) return true;
  const rect = elem.getBoundingClientRect();
  return (
    rect.x === 0 &&
    rect.y === 0 &&
    rect.width === 0 &&
    rect.height === 0 &&
    rect.top === 0 &&
    rect.left === 0
  );
}

export function isElemNotVisible(elem) {
  if (elem == null) return true;
  return elem.hidden || elemHasZeroBoundingRect(elem);
}

/**
 * @desc Returns the Nth parent element of the supplied element (indexing assumes start is 1)
 * @param {Node} elem - The element to retrieve the nth parent element of
 * @param {number} nth - The number of the nth parent
 * @return {?HTMLElement} - The nth parent element if it exists
 */
export function getNthParentElement(elem, nth) {
  if (elem != null && elem.parentElement != null && nth >= 1) {
    let counter = nth - 1;
    let parent = elem.parentElement;
    while (counter > 0 && parent != null) {
      parent = parent.parentElement;
      counter--;
    }
    return parent;
  }
  return null;
}

/**
 *
 * @param {Node} elem
 * @param needle
 * @returns {boolean}
 */
export function elementTextContains(elem, needle) {
  if (elem != null && elem.textContent != null)
    return elem.textContent.includes(needle);
  return false;
}

/**
 *
 * @param {Node} elem
 * @param {string} shouldEqual
 * @returns {boolean}
 */
export function elementTextEqs(elem, shouldEqual) {
  if (elem != null) return elem.textContent === shouldEqual;
  return false;
}

/**
 *
 * @param {Node} elem
 * @param {string} start
 * @param {string} end
 * @returns {boolean}
 */
export function elementTextStartsWithAndEndsWith(elem, start, end) {
  if (elem != null && elem.textContent != null) {
    return elem.textContent.startsWith(start) && elem.textContent.endsWith(end);
  }
  return false;
}

/**
 *
 * @param {Node} elem
 * @param {string} start
 * @returns {boolean}
 */
export function elementTextStartsWith(elem, start) {
  if (elem != null && elem.textContent != null) {
    return elem.textContent.startsWith(start);
  }
  return false;
}

/**
 *
 * @param {Node} elem
 * @param {string} end
 * @returns {boolean}
 */
export function elementTextEndsWith(elem, end) {
  if (elem != null && elem.textContent != null) {
    return elem.textContent.endsWith(end);
  }
  return false;
}

/**
 *
 * @param {HTMLElement} elem
 * @param {string} splitter
 * @returns {?Array<string>}
 */
export function splitElemInnerText(elem, splitter) {
  if (elem != null && elem.innerText != null) {
    return elem.innerText.split(splitter);
  }
  return null;
}

/**
 *
 * @param {Node} elem
 * @param {string} splitter
 * @returns {?Array<string>}
 */
export function splitElemTextContents(elem, splitter) {
  if (elem != null && elem.textContent != null) {
    return elem.textContent.split(splitter);
  }
  return null;
}

/**
 *
 * @param {HTMLElement} elem
 * @returns {?string}
 */
export function elemInnerText(elem) {
  if (elem != null && elem.innerText != null) return elem.innerText;
  return null;
}

/**
 *
 * @param elem
 * @param {string} shouldEqual
 * @param {boolean} [trim = false]
 * @return {boolean}
 */
export function elemInnerTextEqs(elem, shouldEqual, trim = false) {
  if (elem == null || elem.innerText == null) return false;
  const innerText = trim ? elem.innerText.trim() : elem.innerText;
  return innerText === shouldEqual;
}

/**
 *
 * @param elem
 * @param {...string} shouldEquals
 * @return {boolean}
 */
function elemInnerTextEqsOneOf(elem, ...shouldEquals) {
  if (elem != null && elem.innerText != null) return false;
  const innertText = elem.innerText;
  let i = shouldEquals.length;
  while (i--) {
    if (innertText === shouldEquals[i]) return true;
  }
  return false;
}

/**
 *
 * @param {Node} elem
 * @returns {?string}
 */
export function elemTextContent(elem) {
  if (elem != null && elem.textContent != null) return elem.textContent;
  return null;
}

/**
 * @param {?Document} doc
 * @return {{scrollTop: number, scrollLeft: number}}
 */
export function documentScrollPosition(doc) {
  const documentElem = doc != null ? doc : document;
  return {
    scrollTop:
      documentElem.body.scrollTop || documentElem.documentElement.scrollTop,
    scrollLeft:
      documentElem.body.scrollLeft || documentElem.documentElement.scrollLeft
  };
}

/**
 * @desc Helper function to get the actual x and y position,
 * page x and y position (scroll) and the height, width of a given element
 * @param {Element} element
 * @param {Document} [doc]
 * @return {?{y: number, pageY: number, x: number, pageX: number, w: number, h: number}}
 */
export function getElementPositionWidthHeight(element, doc) {
  if (element == null) return null;
  const rect = element.getBoundingClientRect();
  const scrollPos = documentScrollPosition(doc);
  return {
    y: rect.top,
    pageY: rect.top + scrollPos.scrollTop,
    x: rect.left,
    pageX: rect.left + scrollPos.scrollLeft,
    w: rect.width,
    h: rect.height
  };
}

/**
 * @desc Get the position of the passed DOM element
 * @param {Element} element
 * @param {Object} options
 * @return {{clientY: number, clientX: number, pageY: number, pageX: number}}
 */
export function getElementClientPagePosition(element, options) {
  const opts = Object.assign({ x: 1, y: 1, floor: false }, options);
  const cords = getElementPositionWidthHeight(element, opts.doc);
  return {
    clientX: opts.floor
      ? Math.floor(cords.x + (cords.w / 100) * opts.x)
      : cords.x + (cords.w / 100) * opts.x,
    clientY: opts.floor
      ? Math.floor(cords.y + (cords.h / 100) * opts.y)
      : cords.y + (cords.h / 100) * opts.y,
    pageX: opts.floor
      ? Math.floor(cords.pageX + (cords.w / 100) * opts.x)
      : cords.pageX + (cords.w / 100) * opts.x,
    pageY: opts.floor
      ? Math.floor(cords.pageY + (cords.h / 100) * opts.y)
      : cords.pageY + (cords.h / 100) * opts.y
  };
}

/**
 * @desc Get the center of the passed DOM element
 * @param {Element} element
 * @param {{floor: boolean}} [options]
 * @return {?{clientY: number, clientX: number, pageY: number, pageX: number}}
 */
export function getElementClientPageCenter(element, options) {
  if (element == null) return null;
  const opts = Object.assign({ floor: false }, options);
  const cords = getElementPositionWidthHeight(element, opts.doc);
  return {
    clientX: opts.floor
      ? Math.floor(cords.x + cords.w / 2)
      : cords.x + cords.w / 2,
    clientY: opts.floor
      ? Math.floor(cords.y + cords.h / 2)
      : cords.y + cords.h / 2,
    pageX: opts.floor
      ? Math.floor(cords.pageX + cords.w / 2)
      : cords.pageX + cords.w / 2,
    pageY: opts.floor
      ? Math.floor(cords.pageY + cords.h / 2)
      : cords.pageY + cords.h / 2
  };
}

/**
 * @desc Determines if the element the supplied selector selects exists.
 *
 * If one of the supplied selectors matches an existing element the idx property
 * of the returned object is set to the index of the selector in the array and
 * the success property is set to true.
 *
 * Otherwise idx = -1 and success = false.
 * @param {Array<string>} selectors - The query selectors to use for testing if
 * the elements it selects exist
 * @param {SomeElement} [cntx] - Optional element to use rather
 * than the current JS context's document object
 * @return {{idx: number, success: boolean}} - The results of the selectors
 * existence check
 */
export function anySelectorExists(selectors, cntx) {
  const numSelectors = selectors.length;
  for (var i = 0; i < numSelectors; ++i) {
    if (selectorExists(selectors[i], cntx)) {
      return { idx: i, success: true };
    }
  }
  return { idx: -1, success: false };
}

/**
 * @desc Returns T/F indicating if the elements name (localName) is equal to the supplied name
 * @param {Element} elem - The element to check if its name equals the supplied name
 * @param {string} name - The name of the desired element
 * @return {boolean}
 */
export function elementsNameEquals(elem, name) {
  if (!elem) return false;
  return elem.localName === name;
}

/**
 * @desc Returns T/F indicating if the nodes name (nodeName) is equal to the supplied name
 * @param {Node} node - The node to check if its name equals the supplied name
 * @param {string} name - The name of the desired Node
 * @return {boolean}
 */
export function nodesNameEquals(node, name) {
  if (!node) return false;
  return node.nodeName === name;
}

/**
 * @desc Returns the results of evaluating one of the supplied xpath queries if
 * one of the queries yields results otherwise null/undefined
 * @param {{queries: Array<string>, xpg: *, context: *}} options
 * @return {?Array<*>}
 */
export function xpathOneOf({ queries, xpg, context }) {
  let results = null;
  for (var i = 0; i < queries.length; i++) {
    results = xpg(queries[i], context);
    if (results) return results;
  }
  return results;
}

/**
 * @desc Returns the results of querySelector using one of the supplied selectors
 * one if one of the selectors yields results otherwise null/undefined
 * @param {{selectors: Array<string>, context: *}} options
 * @return {?Element}
 */
export function qsOneOf({ selectors, context }) {
  if (selectors == null) return null;
  let results = null;
  for (var i = 0; i < selectors.length; i++) {
    results = qs(selectors[i], context);
    if (results) return results;
  }
  return results;
}

/**
 * @desc Returns the results of querySelectorAll using one of the supplied
 * selectors if one of the selectors yields results otherwise null/undefined
 * @param {{selectors: Array<string>, context: *}} options
 * @return {NodeList<SomeElement>}
 */
export function qsaOneOf({ selectors, context }) {
  if (selectors == null) return null;
  let results = null;
  for (var i = 0; i < selectors.length; i++) {
    results = qsa(selectors[i], context);
    if (results) return results;
  }
  return results;
}

/**
 * @desc Returns the next element sibling of the supplied selector IFF
 * the selector returns a results and the next element sibling exists
 * otherwise null/undefined
 * @param {string} selector - The selector for the element who's next sibling is
 * to be returned
 @param {Element|Node|HTMLElement|Document} [context] - element to use rather than document for the querySelector call
 * @return {?Element}
 */
export function selectedNextElementSibling(selector, context) {
  const maybeSelected = qs(selector, context);
  if (maybeSelected && maybeSelected.nextElementSibling) {
    return maybeSelected.nextElementSibling;
  }
  return null;
}

/**
