export function composeIterators(...funcs) {
  if (funcs.length === 0) {
    return function*() {
      yield arguments[0];
    };
  }

  if (funcs.length === 1) {
    return funcs[0];
  }

  return funcs.reduce(
    (a, b) =>
      function*(...args) {
        yield* a(b(...args));
      }
  );
}

/**
