  const renderedNodes = [];
  const length = elems.length;
  let node;
  let reactInstance;
  for (var i = 0; i < length; ++i) {
    node = elems[i];
    reactInstance = reactInstanceFromDOMElem(node);
    if (selectingFn && selectingFn(reactInstance.key)) {
      renderedNodes.push({ node, reactInstance });
    } else {
      renderedNodes.push({ node, reactInstance });
