  if (parentElement == null) return;
  let nextIterator;
  let next;
  let nextChild = parentElement.firstElementChild;
  while (nextChild != null) {
    nextIterator = fn(nextChild, additionalArgs);
    next = await nextIterator.next();
    while (!next.done) {
      yield next.value;
      next = await nextIterator.next();
    }
    nextChild = nextChild.nextElementSibling;
  }
}
